import { useQuery } from "react-query";
import { fetchCoinHistory } from "../api";
import Apexchart from "react-apexcharts"

interface IHistoricalData {
    time_open: string;
    time_close: number;
    open: number;
    high: number;
    low: number;
    close: number;
    volume: number;
    market_cap: number;
}
interface ChartProps {
    coinId: string;
}
function Chart({ coinId }: ChartProps) {
    const { isLoading, data } = useQuery<IHistoricalData[]>(["ohlcv", coinId], () => fetchCoinHistory(coinId))
    return (
        <div>{isLoading ? "Loading..." :
            <>
            <Apexchart
                type = "line"
                series={[
                    {
                        name: "price",
                        data: data?.map((price => price.close)) ?? [],
                    },
                ]}
                options={{
                    theme: {
                        mode: "dark",
                    },
                    chart: {
                        height: 300,
                        width: 500,
                        toolbar: {
                            show: false
                        },
                        background: "#2c3e50"
                    },
                    stroke: {
                        curve: "smooth",
                        width: 5,
                    },
                    yaxis: {
                        show: false
                    },
                    xaxis: {
                        labels: { show: false },
                        axisTicks: { show: false },
                        axisBorder: { show: false },
                        type: "datetime",
                        categories: data?.map((price) => new Date(price.time_close * 1000).toISOString()),
                    },
                    fill: { type: "gradient", gradient: { gradientToColors: ["#0be881"], stops: [0, 100] } },
                    colors: ["#0fbcf9"],
                    tooltip: {
                        y: {formatter: (value) => `$ ${value.toFixed(2)}`},
                    },
                }} />


                <Apexchart
                type = "line"
                series={[
                    {
                        name: "price",
                        data: data?.map((price => price.close)) ?? [],
                    },
                ]}
                options={{
                    theme: {
                        mode: "dark",
                    },
                    chart: {
                        height: 300,
                        width: 500,
                        toolbar: {
                            show: false
                        },
                        background: "#2c3e50"
                    },
                    stroke: {
                        curve: "smooth",
                        width: 5,
                    },
                    yaxis: {
                        show: false
                    },
                    xaxis: {
                        labels: { show: false },
                        axisTicks: { show: false },
                        axisBorder: { show: false },
                        type: "datetime",
                        categories: data?.map((price) => new Date(price.time_close * 1000).toISOString()),
                    },
                    fill: { type: "gradient", gradient: { gradientToColors: ["#0be881"], stops: [0, 100] } },
                    colors: ["#0fbcf9"],
                    tooltip: {
                        y: {formatter: (value) => `$ ${value.toFixed(2)}`},
                    },
                }} />
                </>
                }
        </div>
    )
}

export default Chart;
